	// 	putchar('\n');
	// }
	if(l==r){c[k]=a[l];return;}
	int mid=(l+r)>>1;
	build(k<<1,l,mid);
	build(k<<1|1,mid+1,r);
	push_up(k);
	// printf("k = %d\n",k);
	// for(int i=1;i<=m;++i)
	// printf("%d ",c[k].d[i]);
	// putchar('\n');
}
int main()
{
	freopen("in.txt","r",stdin);
	freopen("out.txt","w",stdout);
	n=read();
	memset(I.d,0,sizeof(I.d));
	for(int i=1;i<=m;++i)
	I.d[i][i]=1;
	for(int i=1;i<=n;++i)
	{
		a[i].d[1]=read();
		a[i].d[2]=read();
		a[i].d[3]=read();
		a[i].d[4]=1;
	}
	build(1,1,n);
	q=read();
	while(q--)
	{
		int opt=read(),l=read(),r=read();
		if(opt==7)
		{
			ary tmp=query(1,1,n,l,r);
			for(int i=1;i<m;++i)
			printf("%d ",tmp.d[i]);
			putchar('\n');continue;
		}
		mat t=I;
		if(opt==1)t.d[2][1]=1;
		if(opt==2)t.d[3][2]=1;
		if(opt==3)t.d[1][3]=1;
		if(opt==4)t.d[4][1]=read();
