		addd(tree[now].lazy,tree[now].rc,mid+1,tree[now].r);
		tree[now].lazy=dw1;
	}//懒标记下放
	if(l>mid){
		re=summ(tree[now].rc,l,r);
	}
	else if(r<=mid){
		re=summ(tree[now].lc,l,r);
	}
	else{
		re=summ(tree[now].lc,l,mid)+summ(tree[now].rc,mid+1,r);
	}
	return re;
}//区间和
