#include <bits/stdc++.h>
#define int long long//中间的过程会爆int，如果您疯狂取模，会TLE 
using namespace std;
const int mod = 998244353;
const int N = 250010;
int n,m,ans[5][5];//此份代码二维数组的下标都是5，如果想要通过此题，需要令其为4 
struct Mat {
	int a[5][5];
	Mat() {
		memset(a,0,sizeof(a));
	}
	Mat operator * (const Mat &x) {
		Mat c;
		for(register int i = 1; i <= 4; ++i) {
			for(register int j = 1; j <= 4; ++j) {
				for(register int k = 1; k <= 4; ++k) {
					c.a[i][j] = c.a[i][j]+a[i][k]*x.a[k][j]%mod;
					c.a[i][j] -= (c.a[i][j]>=mod) ? mod : 0;//减少取模，卡常 
				}
			}
		}
		return c;
	}
} sum[N<<2],tag[N<<2],unit,tmp;//unit是单位矩阵 

void Unit() {
	for(register int i = 1; i <= 4; ++i) {
		for(register int j = 1; j <= 4; ++j) {
			if(i == j) unit.a[i][j] = 1;
			else unit.a[i][j] = 0;
		}
	}
}

int lc(int x) {
	return x<<1;
}
int rc(int x) {
	return x<<1|1;
}
void pushup(int u) {
	for(register int i = 1; i <= 4; ++i) {
		sum[u].a[1][i] = sum[lc(u)].a[1][i]+sum[rc(u)].a[1][i];
		sum[u].a[1][i] -= (sum[u].a[1][i] >= mod) ? mod : 0;
	}
}
void pushdown(int u) {
	bool flag = true;
	for(register int i = 1; i <= 4; ++i) {
		for(register int j = 1; j <= 4; ++j) {
			if(tag[u].a[i][j] != unit.a[i][j]) {
				flag = false;
				break;
			}
		}
	}
	if(flag) return;//如果tag[u]和单位矩阵相同，则不必转移，转了也没用 
	tag[lc(u)] = tag[lc(u)] * tag[u];
	tag[rc(u)] = tag[rc(u)] * tag[u];
	sum[lc(u)] = sum[lc(u)] * tag[u];
	sum[rc(u)] = sum[rc(u)] * tag[u];
	tag[u] = unit;//清空lazy tag 
}
void build(int u,int l,int r) {
	tag[u] = unit;//初始化lazy tag 
	if(l == r) {
		scanf("%lld",&sum[u].a[1][1]);
        scanf("%lld",&sum[u].a[1][2]);
        scanf("%lld",&sum[u].a[1][3]); 
        //少开点儿数组？ 
        sum[u].a[1][4] = 1;
	} else {
		int mid = (l+r)>>1;
		build(lc(u),l,mid);
		build(rc(u),mid+1,r);
		pushup(u);
	}
}
void modify(int u,int l,int r,int x,int y,Mat v) {
	if(x <= l && y >= r) {
		sum[u] = sum[u]*v;
		tag[u] = tag[u]*v;
		//因为是矩阵乘法，所以顺序不能颠倒！！！ 
    } else {
    	int mid = (l+r)>>1;
		pushdown(u);
    	if(x <= mid) modify(lc(u),l,mid,x,y,v);
    	if(y > mid) modify(rc(u),mid+1,r,x,y,v);
    	pushup(u);
	}
}
void query(int u,int l,int r,int x,int y) {
	if(x <= l && y >= r) {
		for (register int i = 1; i <= 3; i++) {//答案只需要前三个，所以少枚举一次，卡常(？) 
        	ans[1][i] = ans[1][i]+sum[u].a[1][i];
        	ans[1][i] -= (ans[1][i]>=mod) ? mod : 0;
		}
	} else {
		int mid = (l+r)>>1;
		pushdown(u);
		if(x <= mid) query(lc(u),l,mid,x,y);
		if(y > mid) query(rc(u),mid+1,r,x,y);
	}
}

signed main() {
	Unit();
	scanf("%lld",&n);
	build(1,1,n);
	scanf("%lld",&m);
	for(register int i = 1, opt, l, r, v; i <= m; ++i) {
		tmp = unit;
		scanf("%lld%lld%lld",&opt,&l,&r);
		if(opt == 1) {
			tmp.a[2][1] = 1;
			modify(1,1,n,l,r,tmp);
			continue;
		}
		if(opt == 2) {
			tmp.a[3][2] = 1;
			modify(1,1,n,l,r,tmp);
			continue;
		}
		if(opt == 3) {
			tmp.a[1][3] = 1;
			modify(1,1,n,l,r,tmp);
			continue;
		}
		if(opt == 4) {
			scanf("%lld",&v);
			tmp.a[4][1] = v;
			modify(1,1,n,l,r,tmp);
			continue;
		}
		if(opt == 5) {
			scanf("%lld",&v);
			tmp.a[2][2] = v;
			modify(1,1,n,l,r,tmp);
			continue;
		}
		if(opt == 6) {
			scanf("%lld",&v);
			tmp.a[3][3] = 0;
			tmp.a[4][3] = v;
			modify(1,1,n,l,r,tmp);
			continue;
		}
		if(opt == 7) {
			memset(ans,0,sizeof(ans));
			query(1,1,n,l,r);
			printf("%lld %lld %lld\n",ans[1][1],ans[1][2],ans[1][3]);
			continue;
		}
	}
	return 0;
}
```

前三个操作都是对 $\left( A_i, B_i, C_i\right)$ 的线性变换。操作四到六出现了 $+v$ 的操作，考虑经典套路：在行向量中维护一个 $1$, 这样就可以用矩阵乘法维护。

矩阵乘法满足结合律，所以可以上线段树。此时的操作就是区间乘、维护区间和。

**本题不卡常。**

记得乘法 `tag` 要设置成单位矩阵。

```cpp
// 这次只用了 15min 就打完了 
#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

const ll P = 998244353;
template <int N, int M>
struct Matrix {
	ll m[N][M];
	Matrix() { memset(m, 0, sizeof(m)); }
	ll* operator[] (int x) { return m[x - 1] - 1; }
	void one() {
		memset(m, 0, sizeof(m));
		for (int i = 1; i <= min(N, M); ++i)
			(*this)[i][i] = 1;
	}
	void clear() { memset(m, 0, sizeof(m)); }
};
template <int N, int M>
Matrix<N, M> operator + (Matrix<N, M> l, Matrix<N, M> r) {
	Matrix<N, M> m;
	for (int i = 1; i <= N; ++i)
		for (int j = 1; j <= M; ++j)
			m[i][j] = (l[i][j] + r[i][j]) % P;
	return m;
}
template <int N, int K, int M>
Matrix<N, M> operator * (Matrix<N, K>& l, Matrix<K, M>& r) {
	Matrix<N, M> m;
	for (int i = 1; i <= N; ++i)
		for (int j = 1; j <= M; ++j)
			for (int k = 1; k <= K; ++k)
				(m[i][j] += l[i][k] * r[k][j] % P) %= P;
	return m;
}

const int N = 250005;
#define mid ((l + r) >> 1)
#define LS (p << 1)
#define RS ((p << 1) | 1)
Matrix<1, 4> tr[N << 2];
Matrix<4, 4> tag[N << 2];

int n;
ll a[N], b[N], c[N];
void build(int p = 1, int l = 1, int r = n) {
	tag[p].one();
	if (l == r) {
		tr[p][1][1] = a[l];
		tr[p][1][2] = b[l];
		tr[p][1][3] = c[l];
		tr[p][1][4] = 1;
		return;
	}
	build(LS, l, mid);
	build(RS, mid + 1, r);
	tr[p] = tr[LS] + tr[RS];
}
void pushdown(int p) {
	tr[LS] = tr[LS] * tag[p];
	tr[RS] = tr[RS] * tag[p];
	tag[LS] = tag[LS] * tag[p];
	tag[RS] = tag[RS] * tag[p];
	tag[p].one();
}
void mul(int b, int e, Matrix<4, 4>& v, int p = 1, int l = 1, int r = n) {
	if (b <= l && e >= r) {
		tr[p] = tr[p] * v;
		tag[p] = tag[p] * v;
		return ;
	}
	pushdown(p);
	if (b <= mid) mul(b, e, v, LS, l, mid);
	if (e > mid) mul(b, e, v, RS, mid + 1, r);
	tr[p] = tr[LS] + tr[RS];
}
Matrix<1, 4> ans;
void query(int b, int e, int p = 1, int l = 1, int r = n) {
	if (b <= l && e >= r) { ans = ans + tr[p]; return; }
	pushdown(p);
	if (b <= mid) query(b, e, LS, l, mid);
	if (e > mid) query(b, e, RS, mid + 1, r);
}

int main() {	
	scanf("%d", &n);
	for (int i = 1; i <= n; ++i) scanf("%lld%lld%lld", a + i, b + i, c + i);
	build();
	int q; scanf("%d", &q);
	while (q--) {
		int op, l, r, v; scanf("%d%d%d", &op, &l, &r);
		if (op >= 4 && op <= 6) scanf("%d", &v);
		Matrix<4, 4> trs; trs.one();
		switch (op) {
			case 1:
				trs[2][1] = 1; break;
			case 2:
				trs[3][2] = 1; break;
			case 3:
				trs[1][3] = 1; break;
			case 4:
				trs[4][1] = v; break;
			case 5:
				trs[2][2] = v; break;
			case 6:
				trs[3][3] = 0;
				trs[4][3] = v;
				break;
		}
		if (op <= 6) mul(l, r, trs);
		else {
			ans.clear();
			query(l, r);
			printf("%lld %lld %lld\n", ans[1][1], ans[1][2], ans[1][3]);
		}
	}
	return 0;
}
