	while(ch>='0'&&ch<='9'){x=(x<<3)+(x<<1)+ch-'0';ch=getchar();}
	return x*f;
}
int n, m, a[N][N], b[N][N];
ll dis[N<<1];
vector < pair <int, int> > G[N<<1];
struct StPh{
	int cnt[N<<1];
	bool in[N<<1];
	queue <int> Q;
	void clear(){
		memset(cnt, 0, sizeof(cnt));
		memset(in, 0, sizeof(in));
		while(!Q.empty())
			Q.pop();
	}
	bool spfa(){
		dis[1]=0, Q.push(1);
		while(!Q.empty()){
			int x=Q.front();Q.pop();in[x]=0, cnt[x]++;
			if(cnt[x]>n+m) return 1;
			int sz=(int)G[x].size();
			for(int i=0; i<sz; i++){
				int to=G[x][i].first, w=G[x][i].second;
