					dis[to]=dis[x]+w;
					if(!in[to])
						Q.push(to), in[to]=1;
				}

			}
		}
		return 0;
	}
}stph;
void clear(){
	memset(a, 0, sizeof(a));
	memset(dis, 9, sizeof(dis));
