	for(int i=1; i<=n; i++)
		for(int j=1; j<=m; j++){
			if((i+j)&1) 
				G[j+n].push_back(make_pair(i, a[i][j])),
				G[i].push_back(make_pair(j+n, 1e6-a[i][j]));
			else
				G[j+n].push_back(make_pair(i, 1e6-a[i][j])),
				G[i].push_back(make_pair(j+n, a[i][j]));
		}
	if(stph.spfa()) return 0*printf("NO\n");
	printf("YES\n");
