template<class T> bool chmin(T &x, const T &y) {
  return x > y ? (x = y, true) : false;
}
template<class T> bool chmax(T &x, const T &y) {
  return x < y ? (x = y, true) : false;
}

namespace SSSP {
  #define maxV 605
  vector<Pil> adj[maxV];
  int V, cnt[maxV];
  ll dis[maxV];
  bool inque[maxV];

  void init(int n) {
    V = n;
    for (int i = 1; i <= n; i++)
      adj[i].clear();
  }

  void addEdge(int u, int v, ll w) {
    assert(1 <= u && u <= V);
    assert(1 <= v && v <= V);
    adj[u].push_back(mp(v, w));
  }

  bool spfa(int s) { // false <=> 有负环.
    assert(1 <= s && s <= V);
    memset(cnt, 0, sizeof(cnt));
    memset(dis, 0x3f, sizeof(dis));
    memset(inque, 0, sizeof(inque));
    dis[s] = 0;
    queue<int> que;
    que.push(s);
    inque[s] = true;
    while (!que.empty()) {
      int u = que.front(); que.pop();
      inque[u] = false;
      for (auto e : adj[u]) {
        if (chmin(dis[e.first], dis[u] + e.second)) {
          cnt[e.first] = cnt[u] + 1;
          if (cnt[e.first] == V)
            return false;
          if (!inque[e.first]) {
            que.push(e.first);
            inque[e.first] = true;
          }
        }
      }
    }
    return true;
  }
  #undef maxV
}

#define maxn 305
