    if ((i + 1) & 1) {
      SSSP::addEdge(n + 1, i, 0);
      SSSP::addEdge(i, n + 1, LIM);
    } else {
      SSSP::addEdge(n + 1, i, LIM);
      SSSP::addEdge(i, n + 1, 0);
    }
  }
  for (int i = 2; i <= m; i++) {
