    for(int i=1;i<=n;++i)f[i][i]=m+1;
    for(int k=n;k>=1;--k){
        for(int i=1;i<=n;++i){
            if(k>=i)for(int j=1;j<=n;++j)f[i][j]=Max(f[i][j],Min(f[i][k],f[k][j]));
            else for(int j=1;j<=k;++j)f[i][j]=Max(f[i][j],Min(f[i][k],f[k][j]));
        }
    }
    for(int i=1;i<=n;++i){
        for(int j=1;j<=i;++j){
            if(Min(f[i][j],f[j][i]))ans[Min(f[i][j],f[j][i])]++;
        }
