	for(int i=1;i<=n;i++)f[i][i]=m+1;
	for(int i=1,u,v;i<=m;i++)u=read(),v=read(),f[u][v]=i;
	for(int k=n;k;k--){
		for(int i=1;i<=k;i++)
			for(int j=1;j<=n;j++){
