		++t[a];
		maxn=max(maxn,a);
	}
	for (int i=1;i<=maxn;++i)
	{
		if (t[(int)i])
		{
			if (i==1)  ans+=(t[i]*(n-1));
			else
			{
				long long x=(long long)i*2;
				ans+=(t[i]*(t[i]-1));
				while (x<=maxn)
				{
					ans+=(t[i]*t[x]);
					x+=(long long)i;
				}
