int main () {
    read(n);
    if(n > 13) return 0;
    rep(i, 0, n-1) read(val[i]), sum += val[i];
    rep(i, 1, ((1<<n)-1)) {
        la = lb = 0;
        tmp = cnt = 0;
        memset(dp, 0, sizeof(dp));
        rep(j, 0, n-1) {
            if((1<<j) & i) a[++la] = j, tmp += val[j];
            else b[++lb] = j, cnt += val[j];
        }
        dp[0][0] = dp[0][1] = 1;
        rep(j, 1, la) {
            per(k, tmp, 0) {
                if(k >= val[a[j]]) {
                    dp[k][0] |= dp[k-val[a[j]]][0];
                }
            }
        }
        rep(j, 1, lb) {
            per(k, cnt, 0) {
                if(k >= val[b[j]]) {
                    dp[k][1] |= dp[k-val[b[j]]][1];
                }
            }
        }
        now = min_(cnt, tmp);
        per(j, now, 0) {
            if(dp[j][0] && dp[j][1]) {
                ans = min_(ans, sum - j);
                break;
            }
        }   
    }
    out(ans);
}
