	while(T--){
		int n=rd();
		if(n%4!=0){
			printf("w33zAKIOI\n");
			continue;
		}
		judge=n/4;
		if(judge&1){
			printf("2 %d ",-2*judge);
			for(int i=1;i<=3*judge-2;i++)printf("1 ");
			for(int i=1;i<=judge;i++)printf("-1 ");
		}
		else{
			printf("-2 %d ",-2*judge);
			for(int i=1;i<=3*judge;i++)printf("1 ");
