#include <algorithm>

using namespace std;

int T,n,ret[1000005],cnt,sum,dul,num;
bool q;

void Prime (int x)  //分解质因数 
{
    cnt=0;
    for(int i=2;i<=x;i++)
    {
        while(x!=i)
        {
            if(x%i==0)
            {
                ret[++cnt]=i; 
                x=x/i;
            }
            else break;
        }
    }
    ret[++cnt]=x;
}

int abs (int x) 
{
    return x>0 ? x : -x;
}

void solve (int x)
{
    q=1;
    for (int i=1;i<=cnt;++i)
    {
        printf ("%d ",ret[i]);
    }
    for (int i=cnt+1;i<=n;++i)
    {
        if (x)
        {
            printf ("-1 ");
            --x;
        }
        else  printf ("1 ");
    }
    printf ("\n");
}

void dfs (int x)
{
    if (q)  return ;  //已有解 
    if (x>cnt)
    {
        int d=0-sum;
        if (abs(d)>num||((num-abs(d))&1))  return ;  //判断是否符合条件 
        if (d>0)  d=(num-d)/2;
        else  d=(num+d)/2-d;
        if ((dul<0&&(d&1))||(dul>0&&!(d&1)))  solve(d);
        return ;
    }
    if (x&1)  //优化 
    {
        sum+=ret[x];
        dul*=ret[x];
        dfs(x+1);
        sum-=(2*ret[x]);
        dul=-dul;
        ret[x]=-ret[x];
        dfs(x+1);
        ret[x]=-ret[x];
        sum+=ret[x];
        dul=((-dul)/ret[x]);
    }
    else
    {
        sum-=ret[x];
        dul*=(-ret[x]);
        ret[x]=-ret[x];
        dfs(x+1);
        ret[x]=-ret[x];
        sum+=(2*ret[x]);
        dul=-dul;
        dfs(x+1);
        sum-=ret[x];
        dul/=ret[x];
    }
}

int main ()
{
