        }
        num=n-cnt;  //记录1和-1有多少个 
        sum=0;
        dul=1;
        q=0;
        dfs (1);  //深搜+剪枝 
        if (!q)
        {
        	bool p=0;
            for (int j=1;j<cnt;++j)
            {
            	for (int k=j+1;k<=cnt;++k)
				{
					int x=ret[j]+ret[k],y=ret[j]*ret[k];
	                if ((x&1&&y&1)||(!(y&1)&&!(x&1)))  //没搜到，合并再搜一次 
	                {
	                	p=1;
	                    ret[j]=y;
	                    ret[k]=ret[cnt];
	                    --cnt;
	                    break;
	                }
				}
				if (p)  break;
            }
            if (!p)  //无法合并，无解 
            {
            	printf ("w33zAKIOI\n"); 
            	continue;
			}
            num=n-cnt;
            sum=0;
            dul=1;
            q=0;
            dfs (1);
