		}
	}
	int remain = 0;//如果没有开挂，本应有 remain 人存活
	for (register int i = 1; i <= M; i++)
	{
		if (HP[i])
			remain++;
		if (lst[i] != N + 1)//处理一些还未封闭的等价区间
			v[i].push_back(MP(lst[i], N));
		if (die[i] == 0)
			die[i] = N + 5;//如果一直不死，就给他赋一个无限晚的死亡时间
	}
