            //三个优化，尤其是第一个和第三个不能少
            memcpy(drill, lf, sizeof(lf)); //防止干扰
            drill[a[i]]--;
            for(int k = i; k <= n; ++k) {
                if(a[k] == b[k] || drill[a[k]] > 0) --drill[b[k]];
            } //暴力模拟
            cnt[f[j] = calc()]++;
