```cpp
#include<bits/stdc++.h>
using namespace std;
int jia1,jia2;//jia1和jia2表示两个假球的编号 
char ask(int l1,int r1,int l2,int r2)//询问l1~r1与l2~r2之间球的轻重关系。 
{
	printf("1 ");
	printf("%d ",r1-l1+1);//长度 
	for(int i=l1;i<=r1;i++) printf("%d ",i);
	printf("%d ",r2-l2+1);
	for(int i=l2;i<=r2;i++) printf("%d ",i);
	printf("\n");
	char ch; 
	cin>>ch;
	return ch;//读入并返回轻重关系 
}
void print()//输出结果 
{
	if(jia1>jia2) swap(jia1,jia2);//保证先输出的小 
	printf("2 %d %d\n",jia1,jia2);
	exit(0);//直接结束程序 
}
void make_ans(int ans)//将ans设为假球 
{
	if(!jia1) jia1=ans;
	else jia2=ans,print();
}
void find(int l,int r,int num)//l，r该次寻找区间的左右端点，num该区间中假球的数量 
{
	if(l==r)//找的只有一个球，这个球一定是假的 
	{
		make_ans(l);
		return;
	} 
	if(num==1)//找一个假球的情况 
	{
		int midl=l+(r-l)/3;//区间分为三份 
		int midr=r-(r-l)/3;
		char op=ask(l,midl,midr,r);//询问 
		if(op=='=')//相等，假期在剩下那组 
		{
			find(midl+1,midr-1,1);
		}
		if(op=='<')//否则在轻的那组 
		{
			find(l,midl,1);
		}
		if(op=='>')
		{
			find(midr,r,1);
		} 
	} 
	else//找两个假球的情况 
	{
		int mid=(l+r)/2;//先把区间分成二份		
		if((r-l+1)%2==0)//区间长度为偶数的情况 
		{
			char op=ask(l,mid,mid+1,r);//询问
			//以下为题解中的三种情况 
			if(op=='=')
			{
				find(l,mid,1);
				find(mid+1,r,1);
			}
			if(op=='<')
			{
				find(l,mid,2); 
			}
			if(op=='>')
			{
				find(mid+1,r,2);
			}
		}
		else//区间长度为奇数的情况 
		{ 
			char op=ask(l,mid-1,mid+1,r);//询问
			//三种情况 
			if(op=='=')
			{
				find(l,mid-1,1);
				find(mid+1,r,1);
			}
			if(op=='<')
			{
				char opt=ask(mid,mid,mid+1,mid+1);
				//右边（第二组）的球都是真的，利用询问得出剩下的那个是否为假球 
				if(opt=='=')//相等，不是假球 
				{
					find(l,mid-1,2);
				}
				else//不相等，是假球 
				{
					make_ans(mid);
					find(l,mid-1,1);					
				}
			}
			if(op=='>')
			{
				char opt=ask(mid,mid,mid-1,mid-1);
				if(opt=='=')
				{
					find(mid+1,r,2);
				}
				else
				{
					make_ans(mid);
					find(mid+1,r,1);					
				}
			}
		}
	}
}
int main()
{
	int n;
	scanf("%d",&n);
	find(1,n,2);//根据题意，在n个球中找两个假球 
}
