int T, n, a, ans = 0;

int main() {
    scanf("%d", &T);
    while (T --) {
        scanf("%d", &n);
        ans = 0;
        while (n --) {
            scanf("%d", &a);
            ans ^= a; // 计算异或和
        } 
        if (ans) puts("Yes");
        else puts("No");
    }
    return 0;
