} a[10];
bool cmp(S x,S y)
{
    return x.num > y.num;
}
bool cmp1(S x,S y)
{
    return x.id < y.id;
}
int main()
{
    for (int i = 1;i <= 8;i++) cin >> a[i].num,a[i].id = i;
    sort(a + 1,a + 9,cmp);
    int ans = 0;
    for (int i = 1;i <= 5;i++)
        ans += a[i].num;
    cout << ans << endl;
    sort(a + 1,a + 6,cmp1);
