#include<bits/stdc++.h>
using namespace std;
int n, t[3000], tmp, m, s, Ft, St, Fs, Ss;
void Pn_zh(int n) {//将秒数转化成 xx:xx 的形式输出
	printf("%0.2d:%0.2d\n", n/60, n%60);
}
int main() {
	cin >> n;
	for (int i = 1; i <= n; i++) {
		scanf("%d %d:%d", &tmp, &m, &s);
		t[m*60+s] = tmp;
	}
	for (int i = 0; i <= 2880; i++) {//比赛总时间为 48*60=2880 秒
		if (Fs > Ss) Ft++;
		else if (Ss > Fs) St++;
		if (t[i] == 1)Fs++;
		else if (t[i] == 2) Ss++;
	}
	Pn_zh(Ft);
	Pn_zh(St);
}
```

