int n, a, b, t, m, s, d[2885];
int main() {
    scanf("%d", &n);
    for(int i=1; i<=n; i++) {
        scanf("%d%d:%d", &t, &m, &s);
        if(t == 1) d[m*60+s]=1;
        else d[m*60+s]=-1;
    }
    for(int i=0; i<2880; i++) {
        if(i != 0) d[i]+=d[i-1];
        if(d[i] > 0) a++;
        if(d[i] < 0) b++;
    }
    printf("%02d:%02d\n%02d:%02d", a/60, a%60, b/60, b%60);
    return 0;
}
