		}
	}
	int now=max(1,b[1]);	// now 表示横坐标
	int last=1;	// last 表示上一次吃苹果的纵坐标，也就是行数
	int ans=max(0,b[1]-1);	// ans 统计总步数，先统计第一行，第一行毫无疑问是从左走到右
	for(int i=2;i<=rmax;i++){
		if(i%2==0){	//行数为偶数一定是从右走到左
