			ans+=i-last;	//往上走一步或多步，因为可能存在中间某几行都没有苹果的情况
			last=i;
			ans+=abs(b[i]-now);	//求出当前行最右边的位置和上一行最左边的位置
			ans+=abs(b[i]-a[i]);	//把这一行都吃掉
			now=a[i];	//最终停留在这一行的位置，为下一行计算做准备 
		}else{	//行数为奇数一定是从左走到右
			if(b[i]==0)	continue;
			ans+=i-last;
			last=i;
			ans+=abs(a[i]-now);
			ans+=abs(b[i]-a[i]);
			now=b[i];
		}
	}
	cout<<ans;
    return 0;
}

