		int op,x,y;

		cin>>op>>x>>y;

		if(op==1)
		{
			PointChangeA(1,1,n,x,y);
		}
		if(op==2)
		{
			PointChangeB(1,1,n,x,y);
		}
		if(op==3)
		{
			cout<<QueryPsi(1,1,n,x,y).psi<<endl;
		}
	}

	return 0;
}

