	}
	for(int i=1;i<=n;i++){
		cin>>rep[i];
		r[i]=m1[rep[i]];
	}
	for(int i=1;i<n;i++)//核心
		for(int j=i+1;j<=n;j++)
			if(r[i]<r[j])
				cnt++;
	printf("%d/%d",cnt,ttt);
