		sum[i][a]++;
		sum2[i]=sum2[i-1]+(lst==0&&a==1);
		lst=a;
	}
	for(int i=1;i<=n;i++) f[i][0]=sum[i][0]-sum[i-1][1];
	for(int j=1;j<=Log;j++) {
		for(int i=1;i+(1<<j)-1<=n;i++) {
			f[i][j]=max(f[i][j-1],f[i+(1<<(j-1))][j-1]);
		}
	}
	int op,l,r;
	while(m--) {
		op=read();l=read();r=read();
		int ans=0;
		if(op==1) {
			ans=max(1,sum[r][1]-sum[l-1][0]+query(l,r));
			write(ans);putc('\n');
		} else {
			int x;
			if(sum2[l]==sum2[r]) x=1;
			else x=2;
			write(x);putc('\n');
		}
	}
	flush();
	return 0;
}
