}
bool change(int k,int l,int r,int x,int y){
	Add(t[k].sum,y);Add(t[k].sq,sq(y));Add(t[k].cnt,1);
	if(l==r)return true;
	int mid=(l+r)>>1;
	if(x<=mid)return change(t[k].ls(),l,mid,x,y);
