	for(i=1;i<n;i++){
		int x,y;ll z;
		scanf("%d%d%lld",&x,&y,&z);
		add(x,y,z),add(y,x,z);
	}
	dfs(1,0);
	for(i=1;i<=n;i++)D[i]=dep[i];
	sort(dep+1,dep+n+1);
	int tot=unique(dep+1,dep+n+1)-dep-1;
	for(i=1;i<=n;i++)d[i]=lower_bound(dep+1,dep+tot+1,D[i])-dep,D[i]%=mod;
	deal(1,0,1);
	cout<<ZI*qp(MU,mod-2)%mod<<endl;
	return 0;
}
