			}
			else if(g[v][j]<min2)min2=g[v][j];
		}
		if(e[i].w==1){for(int j=1;j<=r;j++)g[u][j]+=min1;}
		else {for(int j=1;j<=r;j++)g[u][j]+=(g[v][j]==min1)?min2:min1;}
	} 
}
