    int x=0,f=1;char ch=getchar();
    while(ch<'0'||ch>'9'){if(ch=='-')f=-1;ch=getchar();}
    while(ch>='0'&&ch<='9')x=x*10+ch-'0',ch=getchar();
    return x*f;
}
using namespace std;
int n,R,fa[N],g;
vector<int>b[N];
vector<int>b1[N];
long long sum=1,Min=100000000000000000;
queue<int>q;
void dfs(int now,int f){
    fa[now]=f;
    for(int i=0;i<b[now].size();i++)
        if(b[now][i]!=f)
                dfs(b[now][i],now);

    return ;
}
void dfs2(int now){
    for(int i=0;i<b[now].size();i++)
        if(b[now][i]!=fa[now]){
            if(b1[now][i]==0)
                g++,dfs2(b[now][i]);
            if(b1[now][i]==1)
                q.push(b[now][i]);
            if(b1[now][i]==2)
                dfs2(b[now][i]);
        }
    return ;
}
long long f[N][200];
void dp(int now){
    for(int i=0;i<b[now].size();i++)
        if(b[now][i]!=fa[now])
            dp(b[now][i]);
    for(int i=1;i<=R;i++){
        f[now][i]=i;
        for(int k=0;k<b[now].size();k++)
            if(b[now][k]!=fa[now]){
                long long m=10000000;
                if(b1[now][k]==0)
                    for(int j=1;j<=R;j++)
                        if(i!=j)
                            m=min(m,f[b[now][k]][j]);
                if(b1[now][k]==1)
                    for(int j=1;j<=R;j++)
                        m=min(m,f[b[now][k]][j]);
                if(b1[now][k]==2)
                    m=f[b[now][k]][i];
                if(m!=10000000)f[now][i]+=m;
            }
    }
    return ;
}
int main(){
    n=read();R=read();
    for(int i=1;i<n;i++){
        int u=read(),v=read(),t=read();
        b[u].push_back(v);b1[u].push_back(t);
        b[v].push_back(u);b1[v].push_back(t);
    }
    dfs(1,1);
    q.push(1);
    while(q.size()){
        g=1;
        int now=q.front();q.pop();
        dfs2(now);
        sum=(sum*R)%mod;
        for(int i=1;i<g;i++)sum=sum*(R-1)%mod;
    }   
    if(sum==0)cout<<"0 0";
    else {
        dp(1);
        for(int i=1;i<=R;i++)if(f[1][i]<Min)Min=f[1][i];
        cout<<sum<<" "<<Min;
    }
    return 0;
}
