char str[MAX][MAX];
int main ()
{
	n = read ();m = read ();a = read (),b = read (),c = read (),d = read ();
	for (int i = 1;i <= n;++i) scanf ("%s",str[i] + 1);
	for (int i = 1;i <= n;++i)
