	{
		node now = q.top ();q.pop ();
		if (now.x < 1 || now.x > n || now.y < 1 || now.y > m) continue;
		if (str[now.x][now.y] == '#') continue;
		if (str[now.x][now.y] == 'E')
		{
			ans = min (ans,now.cos);
