	if(lnk[1].c == 0 && lnk[k].c == 1) ed[++ct] = lnk[1].eid, lsh[++cnt] = lnk[1].eid;
	for(int i = 2; i <= k; ++i) {
		if(lnk[i].c == 1 && lnk[i - 1].c == 0) st[++cs] = lnk[i].eid, lsh[++cnt] = lnk[i].eid;
		if(lnk[i].c == 0 && lnk[i - 1].c == 1) ed[++ct] = lnk[i].eid, lsh[++cnt] = lnk[i].eid;// printf("%d ", i);
