#define int long long
using namespace std;
inline int read(){
	int x=0,f=1;char ch=getchar();
	while(ch<'0'||ch>'9'){if(ch=='-')f=-1;ch=getchar();}
	while(ch>='0'&&ch<='9'){x=x*10+ch-'0';ch=getchar();}
	return x*f;
}
const int N=1e5+10;
int T,n,q,a[N];
struct point{
	int p,id;
}b[N],ans[N];
int cmp1(point a,point b){
	return a.p>b.p;
}
int cmp2(point a,point b){
	return a.id<b.id;
}
signed main()
{
	T=read();
	while(T--){
		n=read(),q=read();
		__int128 sum=0;
		a[0]=0;
