        {
        	for(int k=0;k<=z[5];k++)//枚举NV和VN的
            {
            	for(int l=0;l<=z[6];l++)//枚举OV和VO的
				{
                    ss=0;
                    if(a[i]>z[1]+l+z[4]-j)
					{
