#include<bits/stdc++.h>
using namespace std;
const int maxn=3e3+5;
int n,m,a[maxn],b[maxn],k=maxn;
char s[maxn][maxn];
int main(){
    memset(a,-0x3f,sizeof(a));
		scanf("%d%d",&n,&m);
    for(int i=1;i<=n;++i)
        for(int j=1;j<=m;++j){
            cin>>s[i][j];
            if(s[i][j]=='X')
				a[j]=i;
            else if(s[i][j]=='#'&&!b[j])
				b[j]=i;
        }
    for(int i=1;i<=m;++i)
        k=min(b[i]-a[i]-1,k);
    for(int i=n;i>=1;--i)
        for(int j=m;j>=1;--j)
            if(s[i][j]=='X'){
                s[i+k][j]='X';
                s[i][j]='.';
            }
    for(int i=1;i<=n;++i){
