const int n = maxn - 4;

int t, q, tot, g[maxn], ans[maxn];
bool b[maxn];
// b[x] = 1 说明 x 不能被选

// 若 x中有 7，返回 1 
inline bool pan7(int x) {
	int qwq;
	while(x) {
		qwq = x % 10;
		if(qwq == 7)
			return 1;
		x /= 10;
	}
	return 0;
}

inline void prime() {
	for(int i = 1; i <= n; i++) {
		if(pan7(i)) {
			b[i] = 1;
			for(int j = 1; j <= n; j++) {
				int qwq = i * j;
				if(qwq > n)
					break;
				b[qwq] = 1;
			}
		}
	}
	return;
}

// 快读快写
inline int read() {
    int x = 0, w = 0;
	char ch = 0;
    while(!isdigit(ch)) {
		w |= ch == '-';
		ch = getchar();
	}
    while(isdigit(ch)) {
