			printf("\n");
			for(int i=1;i<=cnt;i++)
				printf(" ");
			printf("%c",s[i]); 
		}
		else if(s[i]==',')
		{
			printf("%c\n",s[i]);
			for(int i=1;i<=cnt;i++) 
				printf(" ");
		}//逗号换行，但不改变缩进的空格数 
		else
			printf("%c",s[i]);//字母直接输出 
