    		int anss=sum(i+k-1,j+k-1)-sum(i+k-1,j-1)-sum(i-1,j+k-1)+sum(i-1,j-1);
 //   		printf("%d %d %d\n",i,j,anss);
    		if(anss>mxans)
    		{
    			mxans=anss;
    			mxi=i-1;
