		for(j=k;j<=m;j++)
		{
			rx=i,ry=j,lx=i-k+1,ly=j-k+1;
			rx--,ry--,lx++,ly++;//注意边界不算
			cnt=s[rx][ry]-s[rx][ly-1]-s[lx-1][ry]+s[lx-1][ly-1];
