#include <bits/stdc++.h>
#define pb push_back
#define rep(i, s, t) for(int i=s; i<=t; i++)
#define F first
#define S second
#define ll long long
#define pii pair<int, int>
#define all(x) x.begin(), x.end()
#define debug(x) cout<<#x<<":"<<x<<endl;
const int N=200010;
using namespace std;

string a, b, c; 
int a1=0, b1=0, c1=0;
int a2=0, b2=0, c2=0;
string s;

void dfs(int i)
{
	if(a1+c1<b2 || a1+b1<c2 || b1+c1<a2) return;
	if(i>=a.size())
	{
		cout<<s;
		exit(0);
	}
	if(a1 && (b[i]=='b' || b[i]=='c'))
	{
		--a1; s+='a';
		if(b[i]=='b') b2--;
		else c2--;
		dfs(i+1);
		if(b[i]=='b') b2++;
		else c2++;
		++a1; s.pop_back();
	}
	if(b1 && (b[i]=='a' || b[i]=='c'))
	{
		--b1; s+='b';
		if(b[i]=='a') a2--;
		else c2--;
		dfs(i+1);
		if(b[i]=='a') a2++;
		else c2++;
		++b1; s.pop_back();
	}
	if(c1 && (b[i]=='a' || b[i]=='b'))
	{
		--c1; s+='c';
		if(b[i]=='a') a2--;
		else b2--;
		dfs(i+1);
		if(b[i]=='a') a2++;
		else b2++;
		++c1; s.pop_back();
	}
}

signed main()
{
