{
    char ch,c=getchar();
	ll v=0,g=1;
    while(c<'0'||c>'9') ch=c,c=getchar();
    if(ch=='-') g=-1;
    while(c>='0'&&c<='9') v=v*10+c-'0',c=getchar();
    return g*v;
}
