#include<bits/stdc++.h>
using namespace std;
int main() {
	int h,min;//读入
	cin>>h>>min;
	int ans=0;
	ans+=h*60+min;//转化同一进制
	ans-=45;
	if(ans<0)ans+=24*60;//如果到了前一天，就变成前一天的最后
	//把单位换回来
