const int N = 1e5 + 1;

int Ans = 0, a[N];

int main() {
	int n; scanf("%d",&n);
	for(int i = 1; i <= n; i++) scanf("%d",a + i);
	for(int i = 1; i <= n; i++) {
		int x; scanf("%d",&x);
		Ans += min(x , a[i]);
	}
	printf("%d\n",Ans);
}
