		for(int i=1;i<=cnt&&prime[i]*prime[i]<=b;i++){
			if(b%prime[i]==0){
				ll num=0;
				while(!(b%prime[i]))b/=prime[i],num++;
				ans=ans*(num*(k-1)%MOD+1)%MOD;
			}
		}
		if(b!=1)ans=ans*k%MOD;
		printf("%lld\n",ans);
	}
	return 0;
}
