char s[400005];
int ans;
int main()
{
	cin>>s;
	int len=strlen(s);
	for(int i=0;i<len;i++)
	    if(s[i]=='c'&&s[i+1]=='h'&&s[i+2]=='u'&&s[i+3]=='a'&&s[i+4]=='n'&&s[i+5]=='z'&&s[i+6]=='h'&&s[i+7]=='i')
	        ans++;
	cout<<ans;
