            }
            for(auto u:pt[i]){
                rep(j,2,sz[u-l+1]){
                    int g=d[u-l+1][j];
                    ll num=occ[g].qry();
                    if(num==-1)continue;
                    if(num*u/g<val[i]){
                        val[i]=num*u/g;to[i]=num-l+1;
                    }
                }
            }
        }
        dfn++;
        rep(i,1,r)occ[i].cur=0;
        per(i,n,1){
            if(Find(i)!=i)continue;
            for(auto u:pt[i])tag[u-l+1]=dfn;
            ll num=occ[1].qry(),u=pt[i][0];
            if(num!=-1){
                if(num*u<val[i]){
                    val[i]=num*u;to[i]=num-l+1;
                }
            }
            for(auto u:pt[i]){
                rep(j,2,sz[u-l+1]){
                    int g=d[u-l+1][j];
                    ll num=occ[g].qry();
                    if(num==-1)continue;
                    if(num*u/g<val[i]){
                        val[i]=num*u/g;to[i]=num-l+1;
                    }
                }
            }
        }
        rep(i,1,tot){
            int u=arr[i];
            int v=to[u];
            ll w=val[u];
            if(Find(u)!=Find(v)){
                Merge(u,v);ans+=w;
                edge++;
            }
        }
    }
}
