#include<bits/stdc++.h>
const int N=1000010;
int st[N],tot,L,R,tp,l,r;
struct edge{
	int u,v;
	long long w;
}e[N*30];
int p[N];
void init(){
	p[1]=l;
	for(int i=2;i<=r;i++)
		for(int j=i;j<=r;j+=i)
			if(j>=l) {p[i]=j;break;}
	for(int i=r;i>=1;i--)
		for(int j=p[i]+i;j<=r;j+=i)
			e[++tp]=(edge){p[i],j,1ll*p[i]*j/i};
}
bool cmp(edge a,edge b) {return a.w<b.w;}
int f[N];
int find(int x){
	while(x!=f[x])x=f[x]=f[f[x]];
	return x;
}
int main() {
	scanf("%d%d",&l,&r);
	init();
	int cnt=0;
	long long ans=0;
	for(int i=l;i<=r;i++)f[i]=i;
	sort(e+1,e+tp+1,cmp);
	for(int i=1;i<=tp;i++){
		int a=find(e[i].u),b=find(e[i].v);
		if(a==b)continue;
		f[a]=b,cnt++,ans+=e[i].w;
		if(cnt==R-L) break;
	}
	printf("%lld\n",ans);
	return 0;
}
