ll read()
{
	ll x=0,f=1;
	char ch=getchar();
	while(ch<'0'||ch>'9')
	{
		if(ch=='-')
			f=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9')
	{
		x=x*10+ch-'0';
		ch=getchar();
	}
	back x*f;
}
ll gcd(ll a,ll b)
{
	if(!b)
		back a;
	back gcd(b,a%b);
}
ll l,r,ans;
int fa[1000005];
ll find(ll x)
{
	if(fa[x]==x)
		back x;
	back fa[x]=find(fa[x]);
}
struct node
{
	ll x,y,t;
}a[10000005];
bool cmp(node a,node b)
{
	back a.t<b.t;
}
int main()
{
	l=read(),r=read();
	for(ri i=l;i<=r;i++)
		fa[i]=i;
	int cnt=0;
	for(ri i=1;i<=r-l+1;i++)
	{
		ll f=0;
		for(ri j=1;j*i<=r;j++)
			if(j*i>=l)
			{
				if(!f)
				{
					f=j*i;
					a[++cnt].x=l,a[cnt].y=f,a[cnt].t=l*f/gcd(l,f);
				}		
				else
					a[++cnt].x=f,a[cnt].y=j*i,a[cnt].t=f*j*i/gcd(f,j*i);
			}
